import React from "react";
import { Link } from "react-router-dom";

const Card = ({ name, username, id }) => {
  const addFav = () => {
    // Aqui iria la logica para guardar la Card en el localStorage
    const favs = JSON.parse(localStorage.getItem("favs")) || [];
    const newFavs = [...favs, { name, username, id }];
    localStorage.setItem("favs", JSON.stringify(newFavs));
  };
  const removeFav = () => {
    // Aqui iria la logica para remover la Card en el localStorage
    const favs = JSON.parse(localStorage.getItem("favs")) || [];
    const newFavs = favs.filter((fav) => fav.id !== id);
    localStorage.setItem("favs", JSON.stringify(newFavs));
  };

  const isFav = () => {
    const favs = JSON.parse(localStorage.getItem("favs")) || [];
    const isFav = favs.find((fav) => fav.id === id);
    return isFav;
  };

  const favButton = isFav() ? (
    <button onClick={removeFav} className="favButton">
      Remove fav
    </button>
  ) : (
    <button onClick={addFav} className="favButton">
      Add fav
    </button>
  );

  return (
    <Link to={`/dentist/${id}`}>
      <div className="card">
        {/* En cada card deberan mostrar en name - username y el id */}
        <h3>{name} </h3>
        <p>{username}</p>
        <p>{id}</p>

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        {favButton}
      </div>
    </Link>
  );
};

export default Card;
